import hashlib, os
multifile_crt = ''
multifile_crt_key = hashlib.sha256(multifile_crt).digest()
multifile_crt_fixed = "Streets paved with the souls of the walking... in this broken city that's how it seems"

def getmfcrt():
    return multifile_crt


def getmfkey(name):
    name = os.path.basename(name)
    return hashlib.sha512(name + name[::-2] + multifile_crt_key + name[::-1]).digest() + hashlib.sha384(multifile_crt_fixed[::-1] + name).digest()