    def makeConveyorCogs(self, lab):
        startPoint = lab.attachNewNode('belt-spawn')
        startPoint.setPosHpr(183.15, 12.65, 3.6, 180.0, 0.0, 0.0)
        if random.randint(0, 100) < 5:
            self.corporateradier = ToontownAvatarUtils.createCog('sf', 0, 0, 0, 0, 0, 0, level=50)
            self.fivePercent = True
        else:
            self.corporateradier = ToontownAvatarUtils.createCog('cr', 0, 0, 0, 0, 0, 0, isSkelecog=True, level=11)
        self.spindoctor = ToontownAvatarUtils.createCog('sd', 0, 0, 0, 0, 0, 0, isSkelecog=True, level=10)
        self.mingler = ToontownAvatarUtils.createCog('m', 0, 0, 0, 0, 0, 0, isSkelecog=True, level=11)
        self.movershaker = ToontownAvatarUtils.createCog('ms', 0, 0, 0, 0, 0, 0, isSkelecog=True, level=9)
        self.myhollywood = ToontownAvatarUtils.createCog('mh', 0, 0, 0, 0, 0, 0, isSkelecog=True, level=12)
        self.bigwig = ToontownAvatarUtils.createCog('bw', 0, 0, 0, 0, 0, 0, isSkelecog=True, level=12)
        self.twoface = ToontownAvatarUtils.createCog('tf', 0, 0, 0, 0, 0, 0, isSkelecog=True, level=10)
        conveyorCogs = [
         self.corporateradier, self.spindoctor, self.mingler,
         self.myhollywood, self.movershaker, self.bigwig, self.twoface]
        for cog in conveyorCogs:
            cog.reparentTo(startPoint)

        return conveyorCogs

    def sendback(self):
        base.localAvatar.sendUpdate('resetEpisodeFlags')
        base.localAvatar.cameraFollow = 0
        base.cr.loadingStuff = 0
        base.cr.inEpisode = False
        base.cr.currentEpisode = None
        base.cr._userLoggingOut = True
        if base.cr.timeManager:
            base.cr.timeManager.setDisconnectReason(ToontownGlobals.DisconnectBookExit)
        base.transitions.fadeScreen(1.0)
        base.cr.gameFSM.request('closeShard')
        return

