    def enterConveyorCogs(self, cogs, belt):
        lerper = NodePath('lerper')
        belt.setTexProjector(TextureStage.getDefault(), NodePath(), lerper)
        interval = lerper.posInterval(31.5, VBase3(9.5, 0, 0))
        interval.start()
        suitInterval = Parallel()
        x = 100
        w = 0
        dur = 30
        for cog in cogs:
            if x <= 80:
                suitInterval.append(Sequence(Wait(w), Wait(1), cog.posInterval(dur, (x, 0, 0))))
            else:
                suitInterval.append(Sequence(Wait(1.5), Wait(w), cog.posInterval(dur, (x, 0, 0))))
            w = w + 3
            x = x - 10
            dur = dur - 3

        suitInterval.start()

