    def toggleDevice(self, state):
        if state == 'hold':
            self.hand = self.toon.find('**/rightHand')
            self.rewrittenDevice.reparentTo(self.hand)
            self.rewrittenDevice.setPosHprScale(0, 0, 0, 0, 0, 0, 0.1, 0.1, 0.1)
            self.rewrittenDevice.show()
        else:
            if state == 'pocket':
                self.rewrittenDevice.hide()
                self.hand = None
        return

    def musicFade(self, song, fadeIn=False, fadeOut=False):
        songTrack = Sequence()
        if fadeIn:
            song.setVolume(0)
            song.play()
            vol = 0
            for v in xrange(0, 101):
                vol += 0.01
                songTrack.append(Sequence(Wait(0.02), Func(song.setVolume, vol)))

        else:
            if fadeOut:
                vol = song.getVolume()
                for v in xrange(0, 101):
                    if vol > 0.0:
                        vol -= 0.01
                        songTrack.append(Sequence(Wait(0.02), Func(song.setVolume, vol)))

                songTrack.append(Func(song.stop))
        songTrack.start()

